import React from "react";
import { useEffect, useState } from "react";
import axios from "axios";
import "./data.css"
import { type } from "@testing-library/user-event/dist/type";


function Datas() {
  const [arama, setArama] = useState();
  const [data, setData] = useState(Date);
  useEffect(() => axios.get("https://api.nasa.gov/planetary/apod?api_key=QglRB5v9ljxlUbvEH06zVrqd9wQBMKU2xwunYzn2")
    .then(res => console.log(res.data))
    .catch(err => console.log(err)), [data, arama])

  return (
    <div className="bar">
      <input placeholder="YYYY-AA-GG" type="date" onChange={(e) => setArama(e.target.value)} />
      {data && (
        <div>
          <h2>{data.title}</h2>
          <img src={data.media-type} alt={data.title} />
          <p>{data.explanation}</p>
        </div>
      )}
    </div>

  )
}



export default Datas;
